mapper.select:
SELECT COUNT(*) AS CNT 
FROM MEMBERS 


		WHERE ID = #{keyword}


		WHERE NAME = #{keyword}


		WHERE PHONE = #{keyword}


		WHERE EMAIL = #{keyword}


		WHERE 1 = 2

SELECT *
  FROM (SELECT ROWNUM AS R_NUM, M.*
          FROM (SELECT *
                  FROM MEMBERS


						WHERE ID = #{keyword}


						WHERE NAME = #{keyword}


						WHERE PHONE = #{keyword}


						WHERE EMAIL = #{keyword}


						WHERE 1 = 2


                 ORDER BY ID
               ) M 
       )
 WHERE R_NUM BETWEEN #{begin} AND #{end}
