mapper.update:
UPDATE BOOKS
SET  book_name = #{book_name }
	, writer = #{writer }
	, publisher = #{publisher }
	, pub_date = #{pub_date }
	, price = #{price }
	, book_desc = #{book_desc }
	, stock = #{stock }
	, category = #{category }
WHERE book_no = #{book_no }

mapper.select:
SELECT * 
 FROM CART 
 WHERE BOOK_NO = #{book_no } 
 AND ID = #{id}

SELECT COUNT(*) 
 FROM CART 
 WHERE ID = #{id} 
 AND BOOK_NO = #{book_no }

SELECT NVL(SUM(CART_PRICE * CART_QUAN), 0)
 FROM CART 
 WHERE ID = #{id }

mapper.update:
	UPDATE CART
	 SET CART_QUAN = #{cart_quan}
 	 WHERE id = #{id} 
 	 AND BOOK_NO = #{book_no}
